mapper.select:
SELECT reservation_capacity_no, reservation_capacity_dw, reservation_capacity_time, reservation_capacity_people FROM ym_reservation_capacity
AND reservation_capacity_dw = #{reservation_capacity_dw}
ORDER BY reservation_capacity_time asc

SELECT reservation_capacity_no, reservation_capacity_dw, reservation_capacity_time, reservation_capacity_people FROM ym_reservation_capacity
AND reservation_capacity_dw = #{reservation_capacity_dw}
ORDER BY reservation_capacity_time asc

mapper.insert:
INSERT INTO ym_reservation_product (
	reservation_no,
	product_no,
	product_cnt
) VALUES (
	#{reservation_no},
	#{product_no},
	#{product_cnt}
)
